const express = require('express')
const app = express()

const consultants = [
    {id:1, name:"consultant 1", description: "description for consultant 1", price: 100},
    {id:2, name:"consultant 2", description: "description for consultant 2", price: 200},
    {id:3, name:"consultant 3", description: "description for consultant 3", price: 250}
]

app.get('/consultants', (req, res) => {
    res.json(consultants)
})

app.get('/consultants/:consultant_id', (req, res) => {
    const consultantId = parseInt(req.params.consultant_id)
    const consultant = consultants.find(consultant => consultant.id === consultantId)

    if(consultant){
        res.json(consultant)
    }else{
        res.status(404).json({ error: "consultant not found"})
    }
})

app.listen(3000, () => {
    console.log("server berjalan")
})